import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import './Foo.css';

class Foo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false
        }

        this.toggleActive = this.toggleActive.bind(this);
    }

    toggleActive = function () {
        this.setState(({
            active: !this.state.active
        }))
    }

    render() {
        return (
            <div>
                <h3 style={{ fontSize:"9rem;  color: #3d405b"}}>Hello from the Foo.</h3>

                {/* Nav Menu Elements */}

                {/* Nav Toggle */}
                <button onClick={this.toggleActive} className={!this.state.active ? 'nav-toggle ' : 'nav-toggle active'}>
                    menu
                    </button>

                {/* Nav Body */}
                <div className={!this.state.active ? 'box ' : 'box active'}>
                    {/* just some space */}
                </div>

                <div className={!this.state.active ? 'box middle' : 'box middle active'}>
                    {/* just some space */}
                </div>
                <div className={!this.state.active ? 'box right' : 'box right active'}>
                    {/* just some space */}
                </div>

                <div className={!this.state.active ? 'modal' : 'modal active'}>
                    {/* just some space */}
                </div>

                {/* Nav List */}
                <div className={!this.state.active ? 'list' : 'list active'}>
                    <div onClick={this.toggleActive} >about</div>
                    <div onClick={this.toggleActive} >work</div>
                    <div onClick={this.toggleActive} >contact</div>
                </div>
            </div>
        )
    }
}

export default Foo
