import React, { Component } from 'react';
import './index.css';

class BlockReveal extends Component {
    constructor(props) {
        super(props);

        this.state = {
            loaded: false
        }
    }


    blockContent = {
        width: this.props.width,
        height: this.props.height,
        backgroundColor: 'red'
    }

    render() {
        console.log(this.props);
        return (
            <div>
                <div className="block-reveal-wrapper" style={this.blockContent}>
                    <h1>{this.props.text}</h1>
                </div>
                <div className="block-reveal-overlay"></div>
            </div>
        )
    }
}

export default BlockReveal
