import React, { Component } from 'react';
import './index.css';

class BlockReveal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: false
    }
    this.toggleActive = this.toggleActive.bind(this);
  }

  toggleActive = function () {
    console.log("toggled active state");
    this.setState(({
      active: !this.state.active
    }))
  }

  render() {
    return (
      <div>
        <h1>'Hi from the block reveal!'</h1>
        <button onClick={this.toggleActive}>Click Me</button>
        <div className="block-container">
          <div className="block-content">
            Content
        </div>
          <div className={this.state.active ? "block-cover active" : "block-cover"}>
            Text content can be placed here.
        </div>
        </div>
        <br />

      </div>
    );
  }

}
export default BlockReveal
