                </div>
                <div className={!this.state.active ? 'box box-3' : 'box box-3 active'}>
                    {/* just some space */}
                </div>
                <div className={!this.state.active ? 'box box-4' : 'box box-4 active'}>
                    {/* just some space */}
                </div>

                <div className={!this.state.active ? 'modal' : 'modal active'}>
                    {/* just some space */}
                </div>

                {/* Nav List */}
                <div className={!this.state.active ? 'list' : 'list active'}>

                            <Link onClick={this.toggleActive} t='' className="list-item">about</Link>

                            <span onClick={this.toggleActive} className="list-item">work</span>

                            <span onClick={this.toggleActive} className="list-item">contact</span>

                            <Footer />
                </div>
            </div>
        )
    }
}

