import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { NavLink } from 'react-router-dom'
import Footer from '../Footer';
import './index.css';

class Nav extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false
        }

        this.toggleActive = this.toggleActive.bind(this);
    }

    toggleActive = function () {
        this.setState(({
            active: !this.state.active
        }))
    }

    render() {
        return (
            <div>

                {/* Nav Menu Elements */}

                {/* Nav Toggle */}
                <button onClick={this.toggleActive} className={!this.state.active ? 'nav-toggle ' : 'nav-toggle active'}>
                    '' <br /> 

                    </button>

                {/* Nav Body */}
                <div className={!this.state.active ? 'box box-0' : 'box box-0 active'}>
                    {/* just some space */}
                </div>

                <div className={!this.state.active ? 'box box-1' : 'box box-1 active'}>
                    {/* just some space */}
                </div>
                <div className={!this.state.active ? 'box box-2' : 'box box-2 active'}>
                    {/* just some space */}
                </div>
                <div className={!this.state.active ? 'box box-3' : 'box box-3 active'}>
                    {/* just some space */}
                </div>
                <div className={!this.state.active ? 'box box-4' : 'box box-4 active'}>
                    {/* just some space */}
                </div>

                <div className={!this.state.active ? 'modal' : 'modal active'}>
                    {/* just some space */}

                </div>

                {/* Nav List */}
                <div className={!this.state.active ? 'list' : 'list active'}>

                            <NavLink activeClassName="nav-link-active" className="nav-link" exact to='/' onClick={this.toggleActive} className="list-item">home</NavLink>

                            <NavLink activeClassName="nav-link-active" className="nav-link" to='/profiles' onClick={this.toggleActive} className="list-item">profiles</NavLink>

                            <Footer />
                </div>
            </div>
        )
    }
}

export default Nav
