import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import './Foo.css';

class Foo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false
        }

        this.toggleActive = this.toggleActive.bind(this);
    }

    toggleActive = function () {
        this.setState(({
            active: !this.state.active
        }))
    }

    render() {
        return (
            <div>
                <h3>Hello from the Foo.</h3>
                <h6>Active state is {this.state.active.toString()}</h6>


                {/* Nav Menu Elements */}

                {/* Nav Toggle */}
                <button onClick={this.toggleActive} className="nav-toggle">
                    menu</button>



                {/* Nav Body */}
                <div className={!this.state.active ? 'box' : 'box active'}>
                    {/* just some space */}
                </div>

                {/* Nav List */}
                <div className={!this.state.active ? 'list' : 'list active'}>
                    <div>about</div>
                    <div>work</div>
                    <div>contact</div>
                </div>

                {/* Nav Modal */}
                <div className={!this.state.active ? 'modal' : 'modal active'}>
                    {/* just some modals */}
                    modal
                </div>
            </div>
        )
    }
}

export default Foo
