import React, { Component } from 'react';
import './index.css';

class BlockReveal extends Component {
    constructor(props) {
        super(props);

        this.state = {
             class: false
        }
    }

    componentDidMount() {
        setTimeout(
            function() {
                console.log('yeet');
                this.setState(({
                    class: true
                }))
            }
        ,1000)

    }

    render() {
        console.log(this.props);
        return (
            <div>
                <div className="block-reveal-wrapper">
                    <h1>{this.props.text}</h1>
                </div>
                <div className={this.state.class ? 'block-reveal-overlay' : 'block-reveal-overlay'}>
                {this.state.class.toString()}
                </div>
            </div>
        )
    }
}

export default BlockReveal
