import React, { Component } from 'react';
import './index.css';

class BlockReveal extends Component {
    constructor(props) {
        super(props);

        this.state = {
             class: false
        }
    }

    componentDidMount() {
        this.setState(({
            class: true
        }))
    }

    render() {
        console.log(this.props);
        return (
            <div>
                <div className="block-reveal-wrapper">
                    <h1>{this.props.text}</h1>
                </div>
                <div className={this.state.class ? 'block-reveal-overlay active' : 'block-reveal-overlay active'}>
                {this.state.class.toString()}
                </div>
            </div>
        )
    }
}

export default BlockReveal
