import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import './Foo.css';

class Foo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false
        }

        this.toggleActive = this.toggleActive.bind(this);
    }

    toggleActive = function () {
        this.setState(({
            active: !this.state.active
        }))
    }

    render() {
        return (
            <div>
                <Container>
                    <Row>
                        <Col lg={9}>
                            <h3 style={{ fontSize: "3rem", color: "black", paddingTop: "8rem" }}>Hello from the Foo.</h3>
                            <p>    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
                        </Col>
                    </Row>
                </Container>
                {/* Nav Menu Elements */}

                {/* Nav Toggle */}
                <button onClick={this.toggleActive} className={!this.state.active ? 'nav-toggle ' : 'nav-toggle active'}>
                    NAV
                    </button>

                {/* Nav Body */}
                <div className={!this.state.active ? 'box box-0' : 'box box-0 active'}>
                    {/* just some space */}
                </div>

                <div className={!this.state.active ? 'box box-1' : 'box box-1 active'}>
                    {/* just some space */}
                </div>
                <div className={!this.state.active ? 'box box-2' : 'box box-2 active'}>
                    {/* just some space */}
                </div>
                <div className={!this.state.active ? 'box box-3' : 'box box-3 active'}>
                    {/* just some space */}
                </div>
                <div className={!this.state.active ? 'box box-4' : 'box box-4 active'}>
                    {/* just some space */}
                </div>

                <div className={!this.state.active ? 'modal' : 'modal active'}>
                    {/* just some space */}
                </div>

                {/* Nav List */}
                <div className={!this.state.active ? 'list' : 'list active'}>
                    <div onClick={this.toggleActive} className="list-item">about -</div>
                    <div onClick={this.toggleActive} className="list-item">work</div>
                    <div onClick={this.toggleActive} className="list-item">contact</div>
                </div>
            </div>
        )
    }
}

export default Foo
