import React, { Component } from 'react';
import './index.css';

class BlockReveal extends Component {
    constructor(props) {
        super(props);

        this.state = {
            loaded: false
        }
    }


    blockContent = {
        width: this.props.width,
        height: this.props.height,
        backgroundColor: 'red'
    }

    blockOverlay = {
        width: this.props.width,
        height: this.props.height,
        backgroundColor: 'white',
        position: 'relative',
        bottom: '0rem',
        zIndex: '1000'
    }

    render() {
        console.log(this.props);
        return (
            <div>
                <div className="block-reveal-wrapper" style={this.blockContent}>
                    <h1>{this.props.text}</h1>


                </div>
                <div className="block-reveal-overlay" style={this.blockOverlay}>
                    Text
                </div>

            </div>
        )
    }
}

export default BlockReveal
