import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import './Foo.css';

class Foo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false
        }

        this.toggleActive = toggleActive.bind(this);
    }

    render() {
        return (
            <Container fluid>
                <Row>
                    <Col>
                    <h3>Hello from the Foo.</h3>
                    {this.state.active.toString()}
                    <button onClick={this.toggleActive = (e) => {
                        this.setState(({
                            active: !this.state.active
                        }))
                    }}>Click</button>
                    </Col>
                </Row>
            </Container>

        )
    }
}

export default Foo
